import React, { Component } from 'react';
import '../style/Widget.css';
import axios from 'axios';


class Formulaire extends Component {
  constructor(props) {
    super(props);
    this.state = {
      nom: "VIDE",
      birth: "",
      followers : "0",
      tab : "",
      value: '',
    places: '0',
    fond: (1),prepa :(1),echau: (1),jour: (6),mois: (2), annee: (2019), heure : (20), lien: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTIIXMhM7wIHrNd1YXmvjzsoadndwcrLBtYsCv-klJjKY5SfRan"};


    this.ArtistSubmit = this.ArtistSubmit.bind(this);
    this.AlbumSubmit = this.AlbumSubmit.bind(this);
    this.TracksSubmit = this.TracksSubmit.bind(this);

        this.nomChange = this.nomChange.bind(this);
        this.birthChange = this.birthChange.bind(this);
        this.followersChange = this.followersChange.bind(this);
        this.tabChange = this.tabChange.bind(this);




  }

  titreaChange(event) {
    this.setState({titre: event.target.value});
  }
  sortieChange(event) {
    this.setState({sortie: event.target.value});
  }
  genreChange(event) {
    this.setState({genre: event.target.value});
  }
  lienChange(event) {
    this.setState({lien: event.target.value});
  }
  auteurChange(event) {
    this.setState({auteur: event.target.value});
  }
  dispoChange(event) {
    this.setState({dispo: event.target.value});
  }
  tabtrackChange(event) {
    this.setState({tabtrack: event.target.value});
  }

  nomChange(event) {
    this.setState({nom: event.target.value});
  }

  birthChange(event) {
    this.setState({birth: event.target.value});
  }

  followersChange(event) {
    this.setState({followers: event.target.value});
  }

  tabChange(event) {
    this.setState({tab: event.target.value});
  }

ArtistSubmit(event) {

  axios.put('http://localhost:3030/artistes', {
    nom: this.state.nom,
    followers: this.state.followers,
    birth: this.state.birth,
    albums: this.state.tab,
  })

    event.preventDefault();
  }



AlbumSubmit(event) {



    event.preventDefault();
  }

TracksSubmit(event) {


    event.preventDefault();
  }

  render() {
    return (

      <div className="Formulaire">
      <h2>Artiste</h2>
      <form onSubmit={this.ArtistSubmit}>
        <label>
          Nom de l'artiste :
            <br/>
          <input type="text" value={this.props.places}  onChange={this.nomChange}  />
        </label>
        <br/>
        <label>
          Date de naissance :
            <br/>
          <input type="date" value={this.props.fond} onChange={this.birthChange} />
        </label>
          <br/>
        <label>
          Nombre de followers :
            <br/>
          <input type="text" value={this.props.prepa} onChange={this.followersChange}  />
        </label>
          <br/>
        <label>
          Ref de l'Album :
            <br/>
          <input type="text" value={this.props.echau}  onChange={this.tabChange}/>
        </label>
        <input type="submit" value="Submit" />
        </form>
          <br/>
          <br/>

                    <form onSubmit={this.handleSubmit}>
                    <h2>Album</h2>
                    <label>
                     Titre :
                       <br/>
                    <input type="text" value={this.props.annee} onChange={this.tabChange}  />
                    </label>
                    <br/>
                    <label>
                      Date de sortie:
                        <br/>
                      <input type="date" value={this.props.annee} onChange={this.tabChange} />
                    </label>
                      <br/>
                    <label>
                      Genre :
                        <br/>
                      <input type="text" value={this.props.mois} onChange={this.tabChange}  />
                    </label>
                      <br/>
                    <label>
                      Lien Cover :
                        <br/>
                      <input type="text" value={this.props.jour} onChange={this.tabChange} />
                    </label>
                      <br/>
                      <label>
                        Auteur:
                          <br/>
                        <input type="text" value={this.props.heure} onChange={this.tabChange}  />
                      </label>
                        <br/>
                        <label>
                          Disponible (oui/non):
                            <br/>
                          <input type="text" value={this.props.heure}  onChange={this.tabChange} />
                        </label>
                          <br/>
                    <label>
                      Reference de tracks:
                        <br/>
                      <input type="text" value={this.props.heure}  />
                    </label>
                      <br/>
                    <label>
                      Lien de la couverture :
                        <br/>
                      <input type="text" value={this.props.lien} />
                    </label>
                    <input type="submit" value="Submit" />
                    </form>
                      <br/>
                      <br/>
                                  <form onSubmit={this.handleSubmit}>
                                  <h2>Track</h2>
                                  <label>
                                   Titre :
                                   <br/>
                                  <input type="text" value={this.props.annee}  />
                                  </label>
                                    <br/>
                                  <label>
                                   Durée :
                                     <br/>
                                  <input type="text" value={this.props.annee}  />
                                  </label>
                                    <br/>
                                  <label>
                                   Nombre d'écoutes :
                                     <br/>
                                  <input type="text" value={this.props.annee}  />
                                  </label>
                                    <br/>
                                  <label>
